"use strict";$(window).on("scroll touchmove",function(){if(0===$(".menu-btn:checked").length){var o=$("section[data-color]"),c=$(document).scrollTop();o.each(function(o,n){var t=$(n);c>=t.position().top&&$("body").css("background",t.attr("data-color"))})}}),$(".menu-btn").click(function(){$("body").toggleClass("noscroll")});